    61→## src/services/livingAppsService.ts
    62→```
    63→// AUTOMATICALLY GENERATED SERVICE
    64→import { APP_IDS } from '@/types/app';
    65→import type { ArtikelEinstellen } from '@/types/app';
    66→
    67→// Base Configuration
    68→const API_BASE_URL = 'https://my.living-apps.de/rest';
    69→
    70→// --- HELPER FUNCTIONS ---
    71→export function extractRecordId(url: string | null | undefined): string | null {
    72→  if (!url) return null;
    73→  // Extrahiere die letzten 24 Hex-Zeichen mit Regex
    74→  const match = url.match(/([a-f0-9]{24})$/i);
    75→  return match ? match[1] : null;
    76→}
    77→
    78→export function createRecordUrl(appId: string, recordId: string): string {
    79→  return `https://my.living-apps.de/rest/apps/${appId}/records/${recordId}`;
    80→}
    81→
    82→async function callApi(method: string, endpoint: string, data?: any) {
    83→  const response = await fetch(`${API_BASE_URL}${endpoint}`, {
    84→    method,
    85→    headers: { 'Content-Type': 'application/json' },
    86→    credentials: 'include',  // Nutze Session Cookies für Auth
    87→    body: data ? JSON.stringify(data) : undefined
    88→  });
    89→  if (!response.ok) throw new Error(await response.text());
    90→  // DELETE returns often empty body or simple status
    91→  if (method === 'DELETE') return true;
    92→  return response.json();
    93→}
    94→
    95→/** Upload a file to LivingApps. Returns the file URL for use in record fields. */
    96→export async function uploadFile(file: File | Blob, filename?: string): Promise<string> {
    97→  const formData = new FormData();
    98→  formData.append('file', file, filename ?? (file instanceof File ? file.name : 'upload'));
    99→  const res = await fetch(`${API_BASE_URL}/files`, {
   100→    method: 'POST',
   101→    credentials: 'include',
   102→    body: formData,
   103→  });
   104→  if (!res.ok) throw new Error(`File upload failed: ${res.status}`);
   105→  const data = await res.json();
   106→  return data.url;
   107→}
   108→
   109→export class LivingAppsService {
   110→  // --- ARTIKEL_EINSTELLEN ---
   111→  static async getArtikelEinstellen(): Promise<ArtikelEinstellen[]> {
   112→    const data = await callApi('GET', `/apps/${APP_IDS.ARTIKEL_EINSTELLEN}/records`);
   113→    return Object.entries(data).map(([id, rec]: [string, any]) => ({
   114→      record_id: id, ...rec
   115→    }));
   116→  }
   117→  static async getArtikelEinstellenEntry(id: string): Promise<ArtikelEinstellen | undefined> {
   118→    const data = await callApi('GET', `/apps/${APP_IDS.ARTIKEL_EINSTELLEN}/records/${id}`);
   119→    return { record_id: data.id, ...data };
   120→  }
   121→  static async createArtikelEinstellenEntry(fields: ArtikelEinstellen['fields']) {
   122→    return callApi('POST', `/apps/${APP_IDS.ARTIKEL_EINSTELLEN}/records`, { fields });
   123→  }
   124→  static async updateArtikelEinstellenEntry(id: string, fields: Partial<ArtikelEinstellen['fields']>) {
   125→    return callApi('PATCH', `/apps/${APP_IDS.ARTIKEL_EINSTELLEN}/records/${id}`, { fields });
   126→  }
   127→  static async deleteArtikelEinstellenEntry(id: string) {
   128→    return callApi('DELETE', `/apps/${APP_IDS.ARTIKEL_EINSTELLEN}/records/${id}`);
   129→  }
   130→
   131→}
