   329→## src/lib/ai.ts
   330→```
   331→const AI_ENDPOINT = "https://my.living-apps.de/litellm/v1/chat/completions";
   332→const AI_MODEL = "default";
   333→
   334→export type ChatMessage = {
   335→  role: "system" | "user" | "assistant";
   336→  content: string | Array<{ type: string; [key: string]: unknown }>;
   337→};
   338→
   339→type CompletionOptions = {
   340→  temperature?: number;
   341→  max_tokens?: number;
   342→  top_p?: number;
   343→  stop?: string[];
   344→  response_format?: { type: string };
   345→};
   346→
   347→export async function chatCompletion(
   348→  messages: ChatMessage[],
   349→  options: CompletionOptions = {}
   350→): Promise<string> {
   351→  const res = await fetch(AI_ENDPOINT, {
   352→    method: "POST",
   353→    headers: { "Content-Type": "application/json", Accept: "application/json" },
   354→    body: JSON.stringify({ model: AI_MODEL, messages, ...options }),
   355→  });
   356→  if (!res.ok) {
   357→    const body = await res.text();
   358→    throw new Error(`AI API ${res.status}: ${body}`);
   359→  }
   360→  const data = await res.json();
   361→  return data.choices[0].message.content;
   362→}
   363→
   364→export async function safeJsonCompletion<T = unknown>(
   365→  messages: ChatMessage[],
   366→  options: CompletionOptions = {}
   367→): Promise<T> {
   368→  const raw = await chatCompletion(messages, options);
   369→  const match = raw.match(/\{[\s\S]*\}|\[[\s\S]*\]/);
   370→  if (!match) throw new Error(`Expected JSON but got: ${raw.slice(0, 200)}`);
   371→  return JSON.parse(match[0]);
   372→}
   373→
   374→export async function withRetry<T>(fn: () => Promise<T>, maxAttempts = 3): Promise<T> {
   375→  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
   376→    try {
   377→      return await fn();
   378→    } catch (err) {
   379→      if (attempt === maxAttempts) throw err;
   380→      await new Promise((r) => setTimeout(r, 1000 * attempt));
   381→    }
   382→  }
   383→  throw new Error("unreachable");
   384→}
   385→
   386→// --- File encoding helpers ---
   387→
   388→export function fileToDataUri(file: File): Promise<string> {
   389→  return new Promise((resolve, reject) => {
   390→    const reader = new FileReader();
   391→    reader.onload = () => resolve(reader.result as string);
   392→    reader.onerror = () => reject(new Error("Failed to read file"));
   393→    reader.readAsDataURL(file);
   394→  });
   395→}
   396→
   397→export async function urlToDataUri(url: string): Promise<string> {
   398→  const res = await fetch(url);
   399→  const blob = await res.blob();
   400→  return new Promise((resolve, reject) => {
   401→    const reader = new FileReader();
   402→    reader.onload = () => resolve(reader.result as string);
   403→    reader.onerror = () => reject(new Error("Failed to encode file"));
   404→    reader.readAsDataURL(blob);
   405→  });
   406→}
   407→
   408→/** Convert a data URI back to a Blob (for file uploads) */
   409→export function dataUriToBlob(dataUri: string): Blob {
   410→  const [header, base64] = dataUri.split(',');
   411→  const mime = header.match(/:(.*?);/)?.[1] ?? 'application/octet-stream';
   412→  const bytes = atob(base64);
   413→  const arr = new Uint8Array(bytes.length);
   414→  for (let i = 0; i < bytes.length; i++) arr[i] = bytes.charCodeAt(i);
   415→  return new Blob([arr], { type: mime });
   416→}
   417→
   418→// --- High-level AI features ---
   419→
   420→export async function classify(
   421→  text: string,
   422→  categories: string[]
   423→): Promise<{ category: string; confidence: number }> {
   424→  return safeJsonCompletion([
   425→    {
   426→      role: "system",
   427→      content: [
   428→        "You are a classifier. Respond ONLY with valid JSON, nothing else.",
   429→        'Output format: {"category": "<one of the allowed categories>", "confidence": <0-1>}',
   430→        `Allowed categories: ${JSON.stringify(categories)}`,
   431→      ].join("\n"),
   432→    },
   433→    { role: "user", content: text },
   434→  ], { temperature: 0 });
   435→}
   436→
   437→export async function extract<T = Record<string, unknown>>(
   438→  text: string,
   439→  schemaDescription: string
   440→): Promise<T> {
   441→  return safeJsonCompletion([
   442→    {
   443→      role: "system",
   444→      content: [
   445→        "You are a data extraction engine. Respond ONLY with valid JSON matching the requested schema.",
   446→        "If a field cannot be determined from the input, use null.",
   447→        `Schema:\n${schemaDescription}`,
   448→      ].join("\n"),
   449→    },
   450→    { role: "user", content: text },
   451→  ], { temperature: 0 });
   452→}
   453→
   454→export async function summarize(
   455→  text: string,
   456→  options: { maxSentences?: number; language?: string } = {}
   457→): Promise<string> {
   458→  const { maxSentences = 3, language } = options;
   459→  const instructions = [
   460→    `Summarize the following text in at most ${maxSentences} sentences.`,
   461→    "Be concise and preserve key facts.",
   462→  ];
   463→  if (language) instructions.push(`Write the summary in ${language}.`);
   464→  return chatCompletion([
   465→    { role: "system", content: instructions.join(" ") },
   466→    { role: "user", content: text },
   467→  ]);
   468→}
   469→
   470→export async function translate(
   471→  text: string,
   472→  targetLanguage: string,
   473→  sourceLanguage?: string
   474→): Promise<string> {
   475→  const from = sourceLanguage ? ` from ${sourceLanguage}` : "";
   476→  return chatCompletion([
   477→    {
   478→      role: "system",
   479→      content: `Translate the following text${from} to ${targetLanguage}. Output ONLY the translation, nothing else.`,
   480→    },
   481→    { role: "user", content: text },
   482→  ]);
   483→}
   484→
   485→export async function analyzeImage(imageDataUri: string, prompt: string): Promise<string> {
   486→  return chatCompletion([
   487→    {
   488→      role: "user",
   489→      content: [
   490→        { type: "text", text: prompt },
   491→        { type: "image_url", image_url: { url: imageDataUri } },
   492→      ],
   493→    },
   494→  ]);
   495→}
   496→
   497→export async function analyzeDocument(fileDataUri: string, prompt: string): Promise<string> {
   498→  return chatCompletion([
   499→    {
   500→      role: "user",
   501→      content: [
   502→        { type: "text", text: prompt },
   503→        { type: "file", file: { file_data: fileDataUri } },
   504→      ],
   505→    },
   506→  ]);
   507→}
   508→
   509→export async function extractFromPhoto<T = Record<string, unknown>>(
   510→  dataUri: string,
   511→  schemaDescription: string
   512→): Promise<T> {
   513→  const isImage = dataUri.startsWith("data:image/");
   514→  const fileContent = isImage
   515→    ? { type: "image_url" as const, image_url: { url: dataUri } }
   516→    : { type: "file" as const, file: { file_data: dataUri } };
   517→  return safeJsonCompletion([
   518→    {
   519→      role: "system",
   520→      content: [
   521→        "Extract structured data from the provided " + (isImage ? "image" : "document") + ".",
   522→        "Respond ONLY with valid JSON matching the schema.",
   523→        "Use null for any field that cannot be determined.",
   524→        `Schema:\n${schemaDescription}`,
   525→      ].join("\n"),
   526→    },
   527→    {
   528→      role: "user",
   529→      content: [
   530→        { type: "text", text: "Extract the data from this " + (isImage ? "image" : "document") + "." },
   531→        fileContent,
   532→      ],
   533→    },
   534→  ]);
   535→}
   536→
